package day04;

public class Inequality_sign_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a=10;
			int b=20;
			int c=30;
			
			System.out.println(a<b); //앞에 있는 문자를 주어로 항상 읽는다.
			//a가 b보다 작은지
			System.out.println(a>b);	//a가 b보닼 큰지
			System.out.println(a+b<=c); //a+b가 c보다 작거나 같은지
			System.out.println(a+b>=c); //a+b가 c보다 크거나 같은지
			
	}

}
